import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators} from '@angular/forms';
import { Router } from '@angular/router';
import { MangasService } from '../mangas.service';

@Component({
  selector: 'app-add-manga',
  templateUrl: './add-manga.component.html',
  styleUrls: ['./add-manga.component.css']
})
export class AddMangaComponent implements OnInit {

  addManga:any;
  constructor(private fb:FormBuilder,
    private routes:Router,
    private mangaservice:MangasService
    ) {
    this.addManga = fb.group(
      {
        title:['',Validators.required],
        author:['',Validators.required],
        year_written:['',Validators.required],
        NumberOfVolumes:['',Validators.required]

      }
    )

     }

  ngOnInit(): void {
  }


  onSubmit() {
    console.log(this.addManga.value)
    this.mangaservice.addManga(this.addManga.value).subscribe((data:any)=>{
      console.log(data);
      this.routes.navigate(['/list-manga'])
    })

  }

}
