import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
import { Router } from '@angular/router';
import { MangasService } from '../mangas.service';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-add-manga',
  templateUrl: './add-manga.component.html',
  styleUrls: ['./add-manga.component.css']
})
export class AddMangaComponent implements OnInit {
  mangaForm: FormGroup;

  constructor(
    private mangaservice: MangasService,
    private router: Router,
    public auth: AuthService
  ) {
    this.mangaForm = new FormGroup({
      title: new FormControl(''),
      author: new FormControl(''),
      year_written: new FormControl(''),
      NumberOfVolumes: new FormControl('')
    });
  }

  ngOnInit(): void {
    if (this.auth.isAuthenticated$) {
      // do something if the user is authenticated
    } else {
      this.auth.loginWithRedirect();
    }
  }

  onSubmit() {
    this.mangaservice.addManga(this.mangaForm.value).subscribe(() => {
      this.router.navigate(['/list-manga']);
    });
  }
}
